mapper.delete:
DELETE
FROM comment_like
WHERE 1 = 1
  AND comment_id = #{commentId}
  AND user_id = #{userId}

mapper.select:
SELECT COUNT(1)
FROM comment_like
WHERE 1 = 1
  AND comment_id = #{commentId}
  AND user_id = #{userId}

SELECT comment_id
FROM comment_like
WHERE 1 = 1
AND user_id = #{userId}
AND comment_id IN

  #{id}
